presets.query:
# Records with images
{
  records_v1 {
    id
    pid
    title
    date_begin
    copyright_holder
    jpg_derivatives {
      signed_url
      filename
    }
    linked_art_jsonld_url
    linked_art_turtle_url
    linked_art_rdfxml_url
  }
}

# Records with PDF files
{
  items_recent(limit: 50) {
    pid
    title
    scope_and_content
    copyright_holder
    jpg_derivatives {
      signed_url
      filename
    }
    pdf_files {
      signed_url
      filename
    }
    linked_art_jsonld_url
    linked_art_turtle_url
    linked_art_rdfxml_url
  }
}

# Single record with attached media + Linked Data
{
  record_v1(id: "19") {
    pid
    title
    payload
    copyright_holder
    linked_art_jsonld_url
    linked_art_turtle_url
    linked_art_rdfxml_url
    attached_media {
      pid
      title
      copyright_holder
      pdf_files {
        signed_url
        filename
      }
      jpg_derivatives {
        signed_url
        filename
      }
      linked_art_jsonld_url
      linked_art_turtle_url
      linked_art_rdfxml_url
    }
  }
}

# Reference: Fonds
{
  ref_fonds {
    id
    code
    label
    notes
  }
}

# Reference: DDR Periods
{
  ref_ddr_period {
    slug
    label
    description
  }
}

# DDR People
{
  agents {
    staff_code
    slug
    display_name
  }
}
